models.Check.prototype.sync = function(method, model, options) {
    var success = options.success, error = options.error;

    // .
    this.setID();

    var Stash = new Bones.plugin.backends.Stash('check');
    Stash.sync.apply(this, arguments);
};

models.Check.prototype.getSuitableNodes = function(options) {
    var success = options.success, error = options.error;
    var check = this;
    
    var Nodes = Bones.plugin.models['Nodes'];
    var nodes = new Nodes();
    nodes.fetch({
        success: function() {
            if (!check.get('sources')) {
                // TODO: filter by node status
                return success(nodes);
            }
            
            var suitableNodes = nodes.filterByLocations(check.get('sources'));
            return success(suitableNodes);
        },
        error: function(collection, err) {
            return error(err);
        }
    });
}